// Receipt status enum
export const RECEIPT_STATUS = {
  0: "Requested",
  // Customer has requested receipt
  1: "Approved",
  // Vendor has approved the request
  2: "Rejected",
  // Vendor has rejected the request
  3: "Verified",
  // Receipt has been verified
  4: "Disputed",
  // Receipt is under dispute
  5: "Cancelled",
  // Receipt has been cancelled
  6: "Expired" // Request has expired
};

// Status colors for UI
export const STATUS_COLORS = {
  0: "warning",
  // Requested - yellow
  1: "primary",
  // Approved - blue
  2: "danger",
  // Rejected - red
  3: "success",
  // Verified - green
  4: "danger",
  // Disputed - red
  5: "secondary",
  // Cancelled - gray
  6: "secondary" // Expired - gray
};

