import React, { useState, useEffect } from 'react';
import { useWeb3 } from '../contexts/Web3Context';
import { RECEIPT_STATUS } from '../config/contract';
import { 
  Receipt, 
  CheckCircle, 
  XCircle, 
  AlertTriangle, 
  Clock, 
  AlertCircle 
} from 'lucide-react';
import toast from 'react-hot-toast';

const BusinessPortal = () => {
  const { contract, account, formatEther } = useWeb3();
  const [pendingReceipts, setPendingReceipts] = useState([]);
  const [allReceipts, setAllReceipts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isRejectModalOpen, setIsRejectModalOpen] = useState(false);
  const [rejectionReason, setRejectionReason] = useState('');
  const [rejectionError, setRejectionError] = useState('');
  const [selectedReceiptId, setSelectedReceiptId] = useState(null);
  const [isDisputeModalOpen, setIsDisputeModalOpen] = useState(false);
  const [disputeReason, setDisputeReason] = useState('');
  const [disputeError, setDisputeError] = useState('');

  useEffect(() => {
    if (contract && account) {
      loadReceipts();
    }
  }, [contract, account]);

  const loadReceipts = async () => {
    try {
      setLoading(true);
      const receiptCount = Number(await contract.getReceiptCount());
      const pending = [];
      const all = [];

      const blockTimestamp = Math.floor(Date.now() / 1000);

      for (let i = 1; i <= receiptCount; i++) {
        const receipt = await contract.getReceipt(i);
        if (receipt.exists && receipt.issuer.toLowerCase() === account.toLowerCase()) {
          const isExpired = receipt.status === 0 && blockTimestamp > Number(receipt.deadline);
          const receiptData = {
            id: Number(receipt.id),
            vendorName: receipt.vendorName,
            description: receipt.description,
            amount: formatEther(receipt.amount),
            status: isExpired ? 6 : Number(receipt.status),
            transactionDate: new Date(Number(receipt.transactionDate) * 1000),
            requestTimestamp: new Date(Number(receipt.requestTimestamp) * 1000),
            deadline: new Date(Number(receipt.deadline) * 1000),
            customer: receipt.recipient,
            rejectionReason: receipt.rejectionReason || '',
            disputeReason: receipt.disputeReason || ''
          };

          all.push(receiptData);
          if (receiptData.status === 0 && !isExpired) {
            pending.push(receiptData);
          }
        }
      }

      setPendingReceipts(pending);
      setAllReceipts(all);
    } catch (error) {
      console.error('Error loading receipts:', error);
      toast.error('Failed to load receipts');
    } finally {
      setLoading(false);
    }
  };

  const handleApproveReceipt = async (receiptId) => {
    try {
      const tx = await contract.approveReceipt(receiptId);
      toast.loading('Approving receipt...', { id: 'approve' });
      await tx.wait();
      toast.success('Receipt approved successfully!', { id: 'approve' });
      await loadReceipts();
    } catch (error) {
      console.error('Error approving receipt:', error);
      toast.error(error.reason || 'Failed to approve receipt', { id: 'approve' });
    }
  };

  const openRejectModal = (receiptId) => {
    setSelectedReceiptId(receiptId);
    setRejectionReason('');
    setRejectionError('');
    setIsRejectModalOpen(true);
  };

  const closeRejectModal = () => {
    setIsRejectModalOpen(false);
    setRejectionReason('');
    setRejectionError('');
    setSelectedReceiptId(null);
  };

  const handleRejectReceipt = async () => {
    if (!rejectionReason.trim()) {
      setRejectionError('Rejection reason cannot be empty');
      return;
    }

    try {
      const tx = await contract.rejectReceipt(selectedReceiptId, rejectionReason.trim());
      toast.loading('Rejecting receipt...', { id: 'reject' });
      await tx.wait();
      toast.success('Receipt rejected successfully!', { id: 'reject' });
      await loadReceipts();
      closeRejectModal();
    } catch (error) {
      console.error('Error rejecting receipt:', error);
      toast.error(error.reason || 'Failed to reject receipt', { id: 'reject' });
    }
  };

  const openDisputeModal = (receiptId) => {
    setSelectedReceiptId(receiptId);
    setDisputeReason('');
    setDisputeError('');
    setIsDisputeModalOpen(true);
  };

  const closeDisputeModal = () => {
    setIsDisputeModalOpen(false);
    setDisputeReason('');
    setDisputeError('');
    setSelectedReceiptId(null);
  };

  const handleDisputeReceipt = async () => {
    if (!disputeReason.trim()) {
      setDisputeError('Dispute reason cannot be empty');
      return;
    }

    try {
      const tx = await contract.disputeReceipt(selectedReceiptId, disputeReason.trim());
      toast.loading('Disputing receipt...', { id: 'dispute' });
      await tx.wait();
      toast.success('Receipt disputed successfully!', { id: 'dispute' });
      await loadReceipts();
      closeDisputeModal();
    } catch (error) {
      console.error('Error disputing receipt:', error);
      toast.error(error.reason || 'Failed to dispute receipt', { id: 'dispute' });
    }
  };

  const canDispute = (receipt) => {
    return receipt.status === 1 || receipt.status === 2 || receipt.status === 3;
  };

  const formatDate = (date) => {
    return date.toLocaleDateString() + ' ' + date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 0: return <Clock className="h-4 w-4 text-yellow-500" />;
      case 1: return <CheckCircle className="h-4 w-4 text-blue-500" />;
      case 2: return <XCircle className="h-4 w-4 text-red-500" />;
      case 3: return <CheckCircle className="h-4 w-4 text-green-500" />;
      case 4: return <AlertTriangle className="h-4 w-4 text-orange-500" />;
      case 5: return <XCircle className="h-4 w-4 text-gray-500" />;
      case 6: return <AlertCircle className="h-4 w-4 text-gray-500" />;
      default: return null;
    }
  };

  const getStatusColor = (status) => {
    const colorMap = {
      0: 'bg-yellow-100 text-yellow-800',
      1: 'bg-blue-100 text-blue-800',
      2: 'bg-red-100 text-red-800',
      3: 'bg-green-100 text-green-800',
      4: 'bg-orange-100 text-orange-800',
      5: 'bg-gray-100 text-gray-800',
      6: 'bg-gray-100 text-gray-800'
    };
    return colorMap[status] || 'bg-gray-100 text-gray-800';
  };

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="text-center py-8">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-2 text-gray-600">Loading receipts...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <h1 className="text-2xl font-bold text-gray-900 mb-6">Business Portal</h1>

      <div className="mb-8">
        <h2 className="text-xl font-semibold text-gray-900 mb-4">Pending Requests</h2>
        {pendingReceipts.length === 0 ? (
          <div className="text-center py-8">
            <Receipt className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">No pending receipts</h3>
            <p className="text-gray-600">There are no pending receipt requests at this time.</p>
          </div>
        ) : (
          <div className="bg-white rounded-lg shadow overflow-hidden">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">ID</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Customer</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Amount</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Description</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Requested</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Actions</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {pendingReceipts.map((receipt) => (
                  <tr key={receipt.id}>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{receipt.id}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{receipt.customer.slice(0, 6)}...{receipt.customer.slice(-4)}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{receipt.amount} ETH</td>
                    <td className="px-6 py-4 text-sm text-gray-500">{receipt.description}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{formatDate(receipt.requestTimestamp)}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                      <button
                        onClick={() => handleApproveReceipt(receipt.id)}
                        className="text-blue-600 hover:text-blue-900 mr-4"
                      >
                        Approve
                      </button>
                      <button
                        onClick={() => openRejectModal(receipt.id)}
                        className="text-red-600 hover:text-red-900"
                      >
                        Reject
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      <div>
        <h2 className="text-xl font-semibold text-gray-900 mb-4">All Requests</h2>
        {allReceipts.length === 0 ? (
          <div className="text-center py-8">
            <Receipt className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">No receipts</h3>
            <p className="text-gray-600">There are no receipts associated with this account.</p>
          </div>
        ) : (
          <div className="bg-white rounded-lg shadow overflow-hidden">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">ID</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Customer</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Amount</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Description</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Status</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Details</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Actions</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {allReceipts.map((receipt) => (
                  <tr key={receipt.id}>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{receipt.id}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{receipt.customer.slice(0, 6)}...{receipt.customer.slice(-4)}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{receipt.amount} ETH</td>
                    <td className="px-6 py-4 text-sm text-gray-500">{receipt.description}</td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStatusColor(receipt.status)}`}>
                        {getStatusIcon(receipt.status)}
                        <span className="ml-1">{RECEIPT_STATUS[receipt.status]}</span>
                      </span>
                    </td>
                    <td className="px-6 py-4 text-sm text-gray-500">
                      {receipt.rejectionReason && receipt.status === 2 && (
                        <p className="text-red-600">Rejection Reason: {receipt.rejectionReason}</p>
                      )}
                      {receipt.disputeReason && receipt.status === 4 && (
                        <p className="text-orange-600">Dispute Reason: {receipt.disputeReason}</p>
                      )}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                      {canDispute(receipt) && (
                        <button
                          onClick={() => openDisputeModal(receipt.id)}
                          className="text-orange-600 hover:text-orange-900"
                        >
                          Dispute
                        </button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* Reject Receipt Modal */}
      {isRejectModalOpen && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-md">
            <h3 className="text-lg font-medium text-gray-900 mb-4">Reject Receipt #{selectedReceiptId}</h3>
            <div className="mb-4">
              <label htmlFor="rejectionReason" className="block text-sm font-medium text-gray-700">
                Reason for Rejection
              </label>
              <textarea
                id="rejectionReason"
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm"
                rows="4"
                value={rejectionReason}
                onChange={(e) => {
                  setRejectionReason(e.target.value);
                  setRejectionError('');
                }}
                placeholder="Enter the reason for rejecting this receipt"
              />
              {rejectionError && (
                <p className="mt-2 text-sm text-red-600">{rejectionError}</p>
              )}
            </div>
            <div className="flex justify-end space-x-3">
              <button
                onClick={closeRejectModal}
                className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 rounded-md hover:bg-gray-200"
              >
                Cancel
              </button>
              <button
                onClick={handleRejectReceipt}
                className="px-4 py-2 text-sm font-medium text-white bg-red-600 rounded-md hover:bg-red-700"
              >
                Confirm Rejection
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Dispute Receipt Modal */}
      {isDisputeModalOpen && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-md">
            <h3 className="text-lg font-medium text-gray-900 mb-4">Dispute Receipt #{selectedReceiptId}</h3>
            <div className="mb-4">
              <label htmlFor="disputeReason" className="block text-sm font-medium text-gray-700">
                Reason for Dispute
              </label>
              <textarea
                id="disputeReason"
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm"
                rows="4"
                value={disputeReason}
                onChange={(e) => {
                  setDisputeReason(e.target.value);
                  setDisputeError('');
                }}
                placeholder="Enter the reason for disputing this receipt"
              />
              {disputeError && (
                <p className="mt-2 text-sm text-red-600">{disputeError}</p>
              )}
            </div>
            <div className="flex justify-end space-x-3">
              <button
                onClick={closeDisputeModal}
                className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 rounded-md hover:bg-gray-200"
              >
                Cancel
              </button>
              <button
                onClick={handleDisputeReceipt}
                className="px-4 py-2 text-sm font-medium text-white bg-orange-600 rounded-md hover:bg-orange-700"
              >
                Confirm Dispute
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default BusinessPortal;
